$(function () {
    function e(e, t, n) {
        var r = $('<figure class="close"></figure>'),
            i = $('<img src="' + t + '"/>');
        i.load(function () {
            e.html('<div class="fullCaption">' + n + "</div>").prepend(i).append(r).slideDown("slow")
        });
        r.on("click", function () {
            $("#fullPreview").slideToggle("slow");
        })
    }
    $("img").lazyload({
        effect: "fadeIn"
    });
    $("#gallery li a").click(function (t) {
        t.preventDefault();
        var n = $(this),
            r = n.attr("href"),
            i = $("#fullPreview"),
            s = i.children("img"),
            o = n.nextAll(".projectInfo").html() || "";
        if (s.length && r === s.attr("src")) {
            i.slideToggle("slow");
            return
        }
        s.length ? i.slideUp("slow", function () {
            e(i, r, o)
        }) : e(i, r, o);
        $('body,html').animate({ scrollTop: 700 }, 800);
    });
});
